const form=document.getElementById("formulario");let linhas="";function adicionaContato(){var e,t=document.getElementById("nome-contato"),n=document.getElementById("numero-contato");nomes.includes(t.value)?alert(`Já existe um contato com este nome "${t.value}"`):numeros.includes(n.value)?alert(`Já existe um contato com este número "${n.value}"`):(nomes.push(t.value),numeros.push(n.value),e="<tr>",e=(e+=`<td>${t.value}</td>`)+`<td>${n.value}</td>`+'<td class="tabela-excluir"><span class="span-excluir">-</span></td></tr>',linhas+=e,t.value="",n.value="")}function atualizaAgenda(){document.getElementById("tabela-body").innerHTML=linhas}nomes=[],numeros=[],form.addEventListener("submit",function(e){e.preventDefault(),adicionaContato(),atualizaAgenda()});const tabela=document.getElementById("tabela-contatos");tabela.addEventListener("click",function(e){e=e.target;e.classList.contains("span-excluir")&&e.parentNode.parentNode.remove()});